	Space_MB   float   NOT NULL
);

INSERT #Free( Drive, Space_MB ) EXEC master.sys.xp_fixeddrives;

SELECT @Avg = MAX( Space_MB ) FROM #Free;

SET @Scale = CASE WHEN @Avg < 1024 THEN 1
