--------------------------------------------------------------------------------
-- Description: Display a chart of database sizes
-- Revision History:
-- ---Date--- ---Programmer---- ---Description----------------------------------
-- 2011-12-24 Katrin Zanevsky	Original public version.
--------------------------------------------------------------------------------
    @Chart_Type  varchar(20)   = 'COLUMN',       -- Recommended types: COLUMN, PIE, or BAR
    @Title       varchar(100)  = null,		 -- Default: "Database size, GB" (or just "Database size" for PIE charts)
    @Skip_Colors varchar(1000) = '2,4,5,6,7,8,11,13,14,18,19,20,23,26,31,37,39,49,52,53,81,83,92' -- Comma-separated list of unwanted colors
AS
SET NOCOUNT ON;

DECLARE @SQL   varchar(8000),
        @Total float;

SET @Title = CASE @Chart_Type 
				  WHEN 'COLUMN' THEN 'Database size, GB'
				  WHEN 'BAR'    THEN 'Database size, GB'
				  WHEN 'PIE'    THEN 'Database size'
		     END;

CREATE TABLE #DB (
	Name                sysname       NOT NULL,
	DB_Size             nvarchar(13)  NOT NULL,
	Owner               sysname       NULL,
	DBID                smallint      NULL,
	Created             nvarchar(11)  NULL,
	Status              nvarchar(600) NULL,
	Compatibility_Level tinyint       NULL
);

INSERT #DB (
		Name,
		DB_Size,
		Owner,
		DBID,
		Created,
		Status,
		Compatibility_Level )
EXEC master.sys.sp_helpdb;

SELECT @Total = SUM( CAST( REPLACE( DB_Size, ' MB', '' ) AS float ) ) FROM #DB;

SET @SQL = CASE @Chart_Type 
				WHEN 'COLUMN' THEN 'SELECT D, X = ROW_NUMBER() OVER ( ORDER BY CASE WHEN D = ''All Other DBs'' THEN 1 ELSE 0 END, S DESC ), S'
				WHEN 'BAR'    THEN 'SELECT D, 0, S'
				WHEN 'PIE'    THEN 'SELECT D, S'
		   END + '
				FROM (	SELECT	D = CASE WHEN CAST ( REPLACE ( DB_Size, '' MB'', '''' ) AS float ) / ' + CAST ( @Total AS varchar(30) ) + ' < 0.05 THEN ''All Other DBs'' ELSE Name END,
								S = SUM ( CAST ( REPLACE ( DB_Size, '' MB'', '''' ) AS float ) ) / 1024.
						FROM	#DB
						GROUP BY CASE WHEN CAST ( REPLACE ( DB_Size, '' MB'', '''' ) AS float ) / ' + CAST ( @Total AS varchar(30) ) + ' < 0.05 THEN ''All Other DBs'' ELSE Name END 
					 ) x
				ORDER BY ' +
		   CASE @Chart_Type 
				WHEN 'COLUMN' THEN 'X'
				ELSE 'CASE WHEN D = ''All Other DBs'' THEN 1 ELSE 0 END, S DESC'
		   END;

EXEC dbo.Chart @SQL = @SQL, @Chart_Type = @Chart_Type, @Title = @Title, @Skip_Colors = @Skip_Colors;
GO

