--------------------------------------------------------------------------------
-- Description: Display a line chart of execution history of a given job
-- Revision History:
-- ---Date--- ---Programmer---- ---Description----------------------------------
-- 2011-12-24 Katrin Zanevsky	Original public version.
--------------------------------------------------------------------------------
    @Job_Name   varchar(128),
    @Start_Date datetime = NULL, -- default = earliest available history of the given job
    @End_Date   datetime = NULL  -- default = now
AS
SET NOCOUNT ON;

DECLARE @SQL varchar(8000), @Title varchar(100), @Max int, @Scale int;

SET @Start_Date = COALESCE( @Start_Date, '1900-01-01' );
SET @End_Date   = COALESCE( @End_Date,   GETDATE() );

CREATE TABLE #Job_History (
    Run_Time         datetime     NOT NULL,
    Run_Duration_Sec int          NOT NULL
);

INSERT	#Job_History( Run_Time, Run_Duration_Sec )
SELECT	Run_Time = CAST( STUFF( STUFF( STR( h.run_date, 8 ), 7, 0, '-' ), 5, 0, '-' ) + ' ' +
				   STUFF( STUFF( RIGHT( STR( 1000000 + h.Run_Time, 7 ), 6 ), 5, 0, ':' ), 3, 0, ':' ) AS datetime ),
		Run_Duration_Sec = h.run_duration % 100 + h.run_duration / 100 % 100 * 60. + h.run_duration / 10000 * 3600.
FROM	msdb.dbo.sysjobhistory h 
JOIN	msdb.dbo.sysjobs j ON h.job_id = j.job_id
WHERE	h.step_id = 0
AND		j.Name = @Job_Name
AND		CAST( STUFF( STUFF( STR( h.run_date, 8 ), 7, 0, '-' ), 5, 0, '-' ) + ' ' +
		STUFF( STUFF( RIGHT( STR( 1000000 + h.Run_Time, 7 ), 6 ), 5, 0, ':' ), 3, 0, ':' ) AS datetime ) BETWEEN @Start_Date AND @End_Date;

SELECT	@Max = MAX( Run_Duration_Sec ) FROM #Job_History;

SET @Scale = CASE WHEN @Max < 120 THEN 1 WHEN @Max < 7200 THEN 60 ELSE 3600 END;

SET @Title = 'Job "' + LEFT( @Job_Name, 43 ) + '" (Y=job duration ' + 
			 CASE @Scale WHEN 1 THEN 'seconds' WHEN 60 THEN 'minutes' ELSE 'hours' END + 
			 ', X=days before ' + CONVERT( char(10), @End_Date, 121 ) + ')';

SET @SQL = 'SELECT	'''',
					X = DATEDIFF( minute, ''' + CONVERT( char(23), GETDATE(), 121 ) + ''', Run_Time ) / 1440.,
					Y = Run_Duration_Sec / ' + CAST( @Scale AS varchar(30) ) + '.
			FROM	#Job_History 
			ORDER BY X';

EXEC dbo.Chart @SQL = @SQL, @Chart_Type = 'LINE', @Title = @Title, @Legend = 0;
GO

