--------------------------------------------------------------------------------
-- Description: Display a bar chart of execution history of all scheduled jobs
-- Revision History:
-- ---Date--- ---Programmer---- ---Description----------------------------------
-- 2011-12-24 Katrin Zanevsky	Original public version.
--------------------------------------------------------------------------------
    @Start_Date       datetime      = NULL, -- Default = 24 hours before now
    @End_Date         datetime      = NULL, -- Default = now
    @Min_Duration_Sec int           = 0,    -- Don't report executions shorter than this
    @Title            varchar(100)  = NULL,
    @Skip_Colors      varchar(1000) = '2,4,5,6,7,8,11,13,14,18,19,20,23,26,31,37,39,49,52,53,81,83,92' -- Comma-separated list of unwanted colors
AS
SET NOCOUNT ON;

SET @Start_Date = COALESCE( @Start_Date, DATEADD ( HOUR, -24, GETDATE() ) );
SET @End_Date   = COALESCE( @End_Date, GETDATE() );
SET @Title		= COALESCE( @Title, 'Scheduled Job History             (X = hours before ' + CONVERT( char(16), @End_Date, 121 ) + ')' );

DECLARE @SQL varchar(8000);

CREATE TABLE #Job_History (
    Name             varchar(128) NOT NULL,
    Run_Time         datetime     NOT NULL,
    Run_Duration_Sec int          NOT NULL
);

INSERT	#Job_History( Name, Run_Time, Run_Duration_Sec )
SELECT	Name, Run_Time, Run_Duration_Sec
FROM (	SELECT	Name = CAST( j.Name AS varchar(128) ),
				Run_Time = CAST( STUFF( STUFF( STR( h.run_date, 8 ), 7, 0, '-' ), 5, 0, '-' ) + ' ' +
						   STUFF( STUFF( RIGHT( STR( 1000000 + h.Run_Time, 7 ), 6 ), 5, 0, ':' ), 3, 0, ':' ) AS datetime ),
				Run_Duration_Sec = h.run_duration % 100 + h.run_duration / 100 % 100 * 60. + h.run_duration / 10000 * 3600.
		FROM	msdb.dbo.sysjobhistory h JOIN msdb.dbo.sysjobs j ON h.job_id = j.job_id
		WHERE	h.step_id = 0
	 ) x
WHERE	Run_Time BETWEEN @Start_Date AND @End_Date
AND		Run_Duration_Sec >= @Min_Duration_Sec;

SET @SQL = 'SELECT	Name,
					X = DATEDIFF( SECOND, ''' + CONVERT( char(23), GETDATE(), 121 ) + ''', Run_Time ) / 3600.,
					Y = Run_Duration_Sec / 3600.
			FROM	#Job_History 
			ORDER BY Name, X';

EXEC dbo.Chart @SQL = @SQL, @Chart_Type = 'BAR', @Title = @Title, @Skip_Colors = @Skip_Colors;
GO

