ALTER PROC Chart_Art
--------------------------------------------------------------------------------
-- Description: Generate a "random" drawing
-- Revision History:
-- ---Date--- ---Programmer---- ---Description----------------------------------
-- 2011-12-24 Andrew Zanevsky	Original public version.
--------------------------------------------------------------------------------
    @Seed  int	    = NULL, -- Use a specific integer seed to reproduce a
                            -- particular drawing or NULL for a random drawing.
    @Color smallint = NULL  -- Color number (execute Chart_Color for help) 
                            -- or NULL for a "random" color defined by @Seed.
AS
DECLARE @SQL varchar(8000), 
		@r1 float, @r2 float, @r3 float, @r4 float, @r5 varchar(3), 
		@Skip_Colors varchar(1000);
		
SET @Seed = COALESCE( @Seed, CAST( CAST( RIGHT( NEWID(), 4 ) AS binary(4) ) AS int ) );
SET @r1 = RAND( @Seed );
SET @r2 = CASE WHEN @Seed % 2 = 0 THEN @r1 ELSE RAND( @r1 * 2147483647 ) END;
SET @r3 = RAND( @r2 * 2147483647 );
SET @r4 = CASE WHEN @Seed % 4 < 2 THEN @r3 ELSE RAND( @r3 * 2147483647 ) END;
SET @r5 = CAST( POWER( 2, CAST( RAND( @r4 * 2147483647 ) * 6 AS int ) ) AS varchar(30) );
SET @Color = COALESCE( @Color, RAND( @r4 * 999999999 ) * 100. + 1 );
SET @Skip_Colors = '';

SELECT Seed = @Seed, Color = @Color;

WHILE @Color > 1 BEGIN;
	SET @Color = @Color - 1;
	SET @Skip_Colors = @Skip_Colors + ',' + CAST( @Color AS varchar(30) );
END;

SET @SQL = '
SELECT	Legend = '''', 
		x = SIN( PI() * n / 512. ) ' + CASE WHEN @r1 < 1./3. THEN '+' WHEN @r1 > 2./3. THEN '-' ELSE '*' END + ' SIN( PI() * n / ' + STR( @r3 * 500 + 1, 10, 0 ) + '. ) * ( n % ' + @r5 + ' / ' + @r5 + '. + 1 ) + SIN( PI() * n / 64. ) * .5,
		y = COS( PI() * n / 512. ) ' + CASE WHEN @r2 < 1./3. THEN '+' WHEN @r2 > 2./3. THEN '-' ELSE '*' END + ' COS( PI() * n / ' + STR( @r4 * 500 + 1, 10, 0 ) + '. ) * ( n % ' + @r5 + ' / ' + @r5 + '. + 1 ) + COS( PI() * n / 64. ) * .5
FROM (	SELECT TOP 1025 n = ROW_NUMBER() OVER ( ORDER BY a.system_type_id ) - 1
		FROM       sys.types a
		CROSS JOIN sys.types b
	 ) n
ORDER BY n';

EXEC dbo.Chart @SQL = @SQL, @Chart_Type = 'PAINT', @Y_Scale = 1, @Y_Grid = 0, @Legend = 0, @Skip_Colors = @Skip_Colors;
GO
